mapper.select:
	
	select 
		rownum rn, board_no, board_title, board_content, board_writer, board_date
	from 
		(select /*+ INDEX_DESC(board pk_board) */ 
			rownum rn, board_no, board_title, board_content, board_writer, board_date
		from board 
		where rownum <= #{pageNum} * #{amount}
		)
	where rn >(#{pageNum} -1) * #{amount}
